package day06

import (
	"fmt"
)

func Day6() {
	input := ""
	fmt.Println(StartOfPacketMarker(input))
}
func StartOfPacketMarker(buffer string) int {
	packet := buffer[0:14]
	x := 14
	for UniqueBuffer(packet) != -1 {
		fmt.Println(packet, UniqueBuffer(packet))
		// TODO: probably could use the index to skip ahead
		x++
		packet = buffer[x-14 : x]
	}

	return x
}

func UniqueBuffer(buffer string) int {
	chars := make(map[rune]int, 14)
	for i, char := range buffer {
		x, ok := chars[char]
		if ok {
			return x
		}
		chars[char] = i
	}
	return -1
}
